var suggestions=document.getElementById('suggestions'),userinput=document.getElementById('userinput');document.addEventListener('keydown',inputFocus);function inputFocus(a){a.keyCode===191&&(a.preventDefault(),userinput.focus()),a.keyCode===27&&(userinput.blur(),suggestions.classList.add('d-none'))}document.addEventListener('click',function(a){var b=suggestions.contains(a.target);b||suggestions.classList.add('d-none')}),document.addEventListener('keydown',suggestionFocus);function suggestionFocus(b){const d=suggestions.querySelectorAll('a'),e=[...d],a=e.indexOf(document.activeElement);let c=0;b.keyCode===38?(b.preventDefault(),c=a>0?a-1:0,d[c].focus()):b.keyCode===40&&(b.preventDefault(),c=a+1<e.length?a+1:a,d[c].focus())}(function(){var a=new FlexSearch.Document({tokenize:"forward",cache:100,document:{id:'id',store:["href","title","description"],index:["title","description","content"]}});a.add({id:0,href:"https://realvincentyuan.github.io/Hugo_blog/docs/prologue/introduction/",title:"Introduction",description:"Doks is a Hugo theme helping you build modern documentation websites that are secure, fast, and SEO-ready — by default.",content:'\u003ch2 id="get-started"\u003eGet started\u003c/h2\u003e\n\u003cp\u003eThere are two main ways to get started with Doks:\u003c/p\u003e\n\u003ch3 id="tutorial"\u003eTutorial\u003c/h3\u003e\n\u003cdiv class="alert alert-warning d-flex" role="alert"\u003e\n  \u003cdiv class="flex-shrink-1 alert-icon"\u003e👉 \u003c/div\u003e\n  \n    \u003cdiv class="w-100"\u003eThe Tutorial is intended for novice to intermediate users. \u003c/div\u003e\n  \n\u003c/div\u003e\n\n\u003cp\u003eStep-by-step instructions on how to start a new Doks project. \u003ca href="https://getdoks.org/tutorial/introduction/"\u003eTutorial →\u003c/a\u003e\u003c/p\u003e\n\u003ch3 id="quick-start"\u003eQuick Start\u003c/h3\u003e\n\u003cdiv class="alert alert-warning d-flex" role="alert"\u003e\n  \u003cdiv class="flex-shrink-1 alert-icon"\u003e👉 \u003c/div\u003e\n  \n    \u003cdiv class="w-100"\u003eThe Quick Start is intended for intermediate to advanced users. \u003c/div\u003e\n  \n\u003c/div\u003e\n\n\u003cp\u003eOne page summary of how to start a new Doks project. \u003ca href="/docs/prologue/quick-start/"\u003eQuick Start →\u003c/a\u003e\u003c/p\u003e\n\u003ch2 id="go-further"\u003eGo further\u003c/h2\u003e\n\u003cp\u003eRecipes, Reference Guides, Extensions, and Showcase.\u003c/p\u003e\n\u003ch3 id="recipes"\u003eRecipes\u003c/h3\u003e\n\u003cp\u003eGet instructions on how to accomplish common tasks with Doks. \u003ca href="https://getdoks.org/docs/recipes/project-configuration/"\u003eRecipes →\u003c/a\u003e\u003c/p\u003e\n\u003ch3 id="reference-guides"\u003eReference Guides\u003c/h3\u003e\n\u003cp\u003eLearn how to customize Doks to fully make it your own. \u003ca href="https://getdoks.org/docs/reference-guides/security/"\u003eReference Guides →\u003c/a\u003e\u003c/p\u003e\n\u003ch3 id="extensions"\u003eExtensions\u003c/h3\u003e\n\u003cp\u003eGet instructions on how to add even more to Doks. \u003ca href="https://getdoks.org/docs/extensions/breadcrumb-navigation/"\u003eExtensions →\u003c/a\u003e\u003c/p\u003e\n\u003ch3 id="showcase"\u003eShowcase\u003c/h3\u003e\n\u003cp\u003eSee what others have build with Doks. \u003ca href="https://getdoks.org/showcase/electric-blocks/"\u003eShowcase →\u003c/a\u003e\u003c/p\u003e\n\u003ch2 id="contributing"\u003eContributing\u003c/h2\u003e\n\u003cp\u003eFind out how to contribute to Doks. \u003ca href="https://getdoks.org/docs/contributing/how-to-contribute/"\u003eContributing →\u003c/a\u003e\u003c/p\u003e\n\u003ch2 id="help"\u003eHelp\u003c/h2\u003e\n\u003cp\u003eGet help on Doks. \u003ca href="/docs/help/how-to-update/"\u003eHelp →\u003c/a\u003e\u003c/p\u003e\n'}).add({id:1,href:"https://realvincentyuan.github.io/Hugo_blog/docs/prologue/quick-start/",title:"Quick Start",description:"One page summary of how to start a new Doks project.",content:'\u003ch2 id="requirements"\u003eRequirements\u003c/h2\u003e\n\u003cp\u003eDoks uses npm to centralize dependency management, making it \u003ca href="/docs/help/how-to-update/"\u003eeasy to update\u003c/a\u003e resources, build tooling, plugins, and build scripts:\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eDownload and install \u003ca href="https://nodejs.org/"\u003eNode.js\u003c/a\u003e (it includes npm) for your platform.\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch2 id="start-a-new-doks-project"\u003eStart a new Doks project\u003c/h2\u003e\n\u003cp\u003eCreate a new site, change directories, install dependencies, and start development server.\u003c/p\u003e\n\u003ch3 id="create-a-new-site"\u003eCreate a new site\u003c/h3\u003e\n\u003cp\u003eDoks is available as a child theme, and a starter theme:\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eUse the Doks child theme, if you do \u003cstrong\u003enot\u003c/strong\u003e plan to customize a lot, and/or need future Doks updates.\u003c/li\u003e\n\u003cli\u003eUse the Doks starter theme, if you plan to customize a lot, and/or do \u003cstrong\u003enot\u003c/strong\u003e need future Doks updates.\u003c/li\u003e\n\u003c/ul\u003e\n\u003cp\u003eNot quite sure? Use the Doks child theme.\u003c/p\u003e\n\u003ch4 id="doks-child-theme"\u003eDoks child theme\u003c/h4\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003egit clone https://github.com/h-enk/doks-child-theme.git my-doks-site\n\u003c/code\u003e\u003c/pre\u003e\n\u003ch4 id="doks-starter-theme"\u003eDoks starter theme\u003c/h4\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003egit clone https://github.com/h-enk/doks.git my-doks-site\n\u003c/code\u003e\u003c/pre\u003e\n\u003ch3 id="change-directories"\u003eChange directories\u003c/h3\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003ecd my-doks-site\n\u003c/code\u003e\u003c/pre\u003e\n\u003ch3 id="install-dependencies"\u003eInstall dependencies\u003c/h3\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003enpm install\n\u003c/code\u003e\u003c/pre\u003e\n\u003ch3 id="start-development-server"\u003eStart development server\u003c/h3\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003enpm run start\n\u003c/code\u003e\u003c/pre\u003e\n\u003cp\u003eDoks will start the Hugo development webserver accessible by default at \u003ccode\u003ehttp://localhost:1313\u003c/code\u003e. Saved changes will live reload in the browser.\u003c/p\u003e\n\u003ch2 id="other-commands"\u003eOther commands\u003c/h2\u003e\n\u003cp\u003eDoks comes with commands for common tasks.\u003c/p\u003e\n'}).add({id:2,href:"https://realvincentyuan.github.io/Hugo_blog/docs/prologue/%E4%B8%80%E8%A1%8C%E4%BB%A3%E7%A0%81%E9%83%BD%E4%B8%8D%E5%86%99%E6%95%99%E4%BD%A0%E4%BD%BF%E7%94%A8github/",title:"一行代码都不写，教你使用GitHub",description:"零基础入门GitHub，一行代码都不写就可以使用GitHub强大的功能",content:'\u003ch2 id="1-github是什么"\u003e1 GitHub是什么\u003c/h2\u003e\n\u003cp\u003e在很多人的印象中，GitHub主要是程序员写代码会使用到的工具。事实上，GitHub最主要的特性是版本管理和协同工作，这意味着不仅是程序员，非技术人士也可以使用它更高效地完成很多工作。因此，这篇文章将教大家零基础使用GitHub，帮助大家认识这个强大的效率工具。首先请熟悉GitHub使用过程中的常见术语：\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003e仓库（Repository）：可以看作是存代码和文件一个文件夹，当你作为仓库管理者时，可以设置访问权限\u003c/li\u003e\n\u003cli\u003e远程仓库（Remote Repository）：可以看作仓库的副本，通常在此完成修改，日后用于补充到仓库主干上\u003c/li\u003e\n\u003cli\u003e主干（master或main）：当前项目所在的状态\u003c/li\u003e\n\u003cli\u003e分支（Branch）：可以看作主干的副本，可暂时存储修改的状态，常用于针对主干做更新\u003c/li\u003e\n\u003cli\u003e提交更新（Push）：将修改提交到仓库中\u003c/li\u003e\n\u003cli\u003e获取更新（Pull）：将仓库上的更新同步到现在的工作状态\u003c/li\u003e\n\u003cli\u003e合并请求（Pull request）：用于将分支上的修改合并到主干上\u003c/li\u003e\n\u003cli\u003e合并（Merged）：分支上的更改被合并到主干上，仓库状态更新\u003c/li\u003e\n\u003c/ul\u003e\n\u003cfigure\u003e\n  \u003cimg src="https://cdn.jsdelivr.net/gh/BulletTech2021/Pics/2021-7-17/1626489355119-Github%20%E5%9B%BE%E4%BE%8B.png" width="600" /\u003e\n  \u003cfigcaption\u003eGitHub 常见术语图示\u003c/figcaption\u003e\n\u003c/figure\u003e\n\u003cp\u003e\u003cimg src="https://cdn.jsdelivr.net/gh/BulletTech2021/Pics/2021-7-17/1626489355119-Github%20%E5%9B%BE%E4%BE%8B.png" alt="GitHub 常见术语图示" title="GitHub 常见术语图示"\u003e\u003c/p\u003e\n\u003cp\u003e接下来，将主要会使用GitHub的桌面客户端（在GitHub的官方网站可免费下载）进行功能介绍，借助这个app，可以使用GitHub绝大多数的核心功能。\u003c/p\u003e\n\u003ch2 id="2-github能做什么"\u003e2 GitHub能做什么\u003c/h2\u003e\n\u003cp\u003eGitHub在版本管理和协同工作方面是绝佳的工具之一。\u003c/p\u003e\n\u003ch3 id="21-版本管理"\u003e2.1 版本管理\u003c/h3\u003e\n\u003cp\u003e具体来说，版本管理的功能可以帮助保存文件的修改历史，以便在需要回溯时能很方便地检查和回滚。\u003c/p\u003e\n\u003cfigure\u003e\n  \u003cimg src="https://cdn.jsdelivr.net/gh/BulletTech2021/Pics/2021-7-17/1626489404945-Github%20%E6%9F%A5%E7%9C%8B%E5%8E%86%E5%8F%B2%E7%89%88%E6%9C%AC.png" width="600" /\u003e\n  \u003cfigcaption\u003eGitHub Desktop查看历史版本\u003c/figcaption\u003e\n\u003c/figure\u003e\n\u003cp\u003e示例中是BulletTech的仓库，过往的更改历史都被保存了下来，点击更改即可看到更改的文件和对应的更新。\u003c/p\u003e\n\u003ch3 id="22-协作"\u003e2.2 协作\u003c/h3\u003e\n\u003cp\u003e这些更改是由BulletTech的团队成员共同完成的，当多人协作时，建议在不同的分支上工作，当完成更新后，可以通过合并请求（⌘/Ctrl + R）将你的分支上的更新合并到主干上。此时，软件会自动引导你跳转到网页端创建请求，团队的成员就可以审核更改，如果符合要求，更新就可以被合并到主干上。\u003c/p\u003e\n\u003cfigure\u003e\n  \u003cimg src="https://cdn.jsdelivr.net/gh/BulletTech2021/Pics/2021-7-17/1626489452783-Github%E5%90%88%E5%B9%B6%E8%AF%B7%E6%B1%82.png" width="600" /\u003e\n  \u003cfigcaption\u003e合并请求\u003c/figcaption\u003e\n\u003c/figure\u003e\n\u003cp\u003e建议设置合并请求的模板，将更新的信息填写清楚会节省很多沟通的时间，日后回看时也有迹可循。可以在我们的\u003ca href="https://GitHub.com/BulletTech/BulletTech/tree/main/.GitHub"\u003e仓库\u003c/a\u003e中找到对应的模板，并根据自己的需求对模板进行修改，运用在你自己的项目中。\u003c/p\u003e\n\u003ch3 id="23-建立博客"\u003e2.3 建立博客\u003c/h3\u003e\n\u003cp\u003eGitHub提供了免费的服务器承载简单的博客，BulletTech自己的博客就搭建在了GitHub上。你只需要在仓库的设置选项中开启GitHub Pages功能，博客就自动生成了，GitHub提供了很多的模板供选择，同时你可以自己从头编写，此处不再展开，如有兴趣，请前往我们的\u003ca href="https://bullettech.GitHub.io/BulletTech/"\u003e仓库\u003c/a\u003e查看源码进行学习。\u003c/p\u003e\n\u003cfigure\u003e\n  \u003cimg src="https://cdn.jsdelivr.net/gh/BulletTech2021/Pics/2021-6-24/1624539190066-Blog.jpeg" width="300" /\u003e\n  \u003cfigcaption\u003eBulletTech博客\u003c/figcaption\u003e\n\u003c/figure\u003e\n\u003ch2 id="3-github怎么用"\u003e3 GitHub怎么用\u003c/h2\u003e\n\u003ch3 id="31-创建仓库"\u003e3.1 创建仓库\u003c/h3\u003e\n\u003cp\u003e注册完GitHub后，在你的主页里，点击最显眼的绿色按钮（New）即可新建仓库了。点击进入仓库后，点击绿色按钮（Code）即可使用GitHub Desktop软件打开，并把文件下载到你的电脑上，接下来你就可以正式开展项目了！\u003c/p\u003e\n\u003cfigure\u003e\n  \u003cimg src="https://cdn.jsdelivr.net/gh/BulletTech2021/Pics/2021-6-24/1624540363015-%E5%88%9B%E5%BB%BA%E4%BB%93%E5%BA%93.png" width="600" /\u003e\n  \u003cfigcaption\u003e新建仓库\u003c/figcaption\u003e\n\u003c/figure\u003e\n\u003cfigure\u003e\n  \u003cimg src="https://user-images.GitHub\nusercontent.com/84658804/123267740-ecebfd00-d52f-11eb-85d9-b2f90583bf4c.png" width="600" /\u003e\n  \u003cfigcaption\u003e用GitHub\n Desktop打开仓库\u003c/figcaption\u003e\n\u003c/figure\u003e\n\u003ch3 id="32-github-desktop基本功能"\u003e3.2 GitHub Desktop基本功能\u003c/h3\u003e\n\u003cp\u003e基本操作如下图所示。请注意，有时同步（Pull)仓库时会发生冲突，原因大多是本地的更新没有同步到分支上，而其他人已经更新的分支，这时，在同步前需要将你的更新搁置（Stash）或者放弃（Discard），这两个操作可以在顶部Branch菜单里找到。\u003c/p\u003e\n\u003cfigure\u003e\n  \u003cimg src="https://cdn.jsdelivr.net/gh/BulletTech2021/Pics/2021-6-26/1624676128227-%E5%9F%BA%E6%9C%AC%E6%93%8D%E4%BD%9C.png" width="600" /\u003e\n  \u003cfigcaption\u003eGitHub\n Desktop基本操作\u003c/figcaption\u003e\n\u003c/figure\u003e\n\u003cp\u003e综上所述，掌握了这些基本知识和技能后，你能够很顺利地使用GitHub进行文件管理、协同合作、创建自己的博客，到目前为止，一行代码都不要写。当然，使用代码也可以很方便地操作GitHub，下次我们将使用命令操作GitHub，敬请期待！\u003c/p\u003e\n\u003cfigure\u003e\n  \u003cimg src="https://cdn.jsdelivr.net/gh/BulletTech2021/Pics/2021-6-14/1623639526512-1080P%20(Full%20HD)%20-%20Tail%20Pic.png" width="500" /\u003e\n\u003c/figure\u003e\n'}).add({id:3,href:"https://realvincentyuan.github.io/Hugo_blog/docs/help/how-to-update/",title:"How to Update",description:"Regularly update the installed npm packages to keep your Doks website stable, usable, and secure.",content:'\u003cdiv class="alert alert-warning d-flex" role="alert"\u003e\n  \u003cdiv class="flex-shrink-1 alert-icon"\u003e💡 \u003c/div\u003e\n  \n    \u003cdiv class="w-100"\u003eLearn more about \u003ca href="https://docs.npmjs.com/about-semantic-versioning"\u003esemantic versioning\u003c/a\u003e and \u003ca href="https://docs.npmjs.com/cli/v6/using-npm/semver#advanced-range-syntax"\u003eadvanced range syntax\u003c/a\u003e. \u003c/div\u003e\n  \n\u003c/div\u003e\n\n\u003ch2 id="check-for-outdated-packages"\u003eCheck for outdated packages\u003c/h2\u003e\n\u003cp\u003eThe \u003ca href="https://docs.npmjs.com/cli/v7/commands/npm-outdated"\u003e\u003ccode\u003enpm outdated\u003c/code\u003e\u003c/a\u003e command will check the registry to see if any (or, specific) installed packages are currently outdated:\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003enpm outdated [[\u0026lt;@scope\u0026gt;/]\u0026lt;pkg\u0026gt; ...]\n\u003c/code\u003e\u003c/pre\u003e\n\u003ch2 id="update-packages"\u003eUpdate packages\u003c/h2\u003e\n\u003cp\u003eThe \u003ca href="https://docs.npmjs.com/cli/v7/commands/npm-update"\u003e\u003ccode\u003enpm update\u003c/code\u003e\u003c/a\u003e command will update all the packages listed to the latest version (specified by the tag config), respecting semver:\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003enpm update [\u0026lt;pkg\u0026gt;...]\n\u003c/code\u003e\u003c/pre\u003e\n'}).add({id:4,href:"https://realvincentyuan.github.io/Hugo_blog/docs/help/troubleshooting/",title:"Troubleshooting",description:"Solutions to common problems.",content:'\u003ch2 id="problems-updating-npm-packages"\u003eProblems updating npm packages\u003c/h2\u003e\n\u003cp\u003eDelete the \u003ccode\u003e./node_modules\u003c/code\u003e folder, and run again:\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003enpm install\n\u003c/code\u003e\u003c/pre\u003e\n\u003ch2 id="problems-with-cache"\u003eProblems with cache\u003c/h2\u003e\n\u003cp\u003eDelete the temporary directories:\u003c/p\u003e\n\u003cpre\u003e\u003ccode class="language-bash"\u003enpm run clean\n\u003c/code\u003e\u003c/pre\u003e\n'}).add({id:5,href:"https://realvincentyuan.github.io/Hugo_blog/docs/help/faq/",title:"FAQ",description:"Answers to frequently asked questions.",content:'\u003ch2 id="hyas"\u003eHyas?\u003c/h2\u003e\n\u003cp\u003eDoks is a \u003ca href="https://gethyas.com/themes/"\u003eHyas theme\u003c/a\u003e build by the creator of Hyas.\u003c/p\u003e\n\u003ch2 id="footer-notice"\u003eFooter notice?\u003c/h2\u003e\n\u003cp\u003ePlease keep it in place.\u003c/p\u003e\n\u003ch2 id="keyboard-shortcuts-for-search"\u003eKeyboard shortcuts for search?\u003c/h2\u003e\n\u003cul\u003e\n\u003cli\u003efocus: \u003ccode\u003e/\u003c/code\u003e\u003c/li\u003e\n\u003cli\u003eselect: \u003ccode\u003e↓\u003c/code\u003e and \u003ccode\u003e↑\u003c/code\u003e\u003c/li\u003e\n\u003cli\u003eopen: \u003ccode\u003eEnter\u003c/code\u003e\u003c/li\u003e\n\u003cli\u003eclose: \u003ccode\u003eEsc\u003c/code\u003e\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch2 id="other-documentation"\u003eOther documentation?\u003c/h2\u003e\n\u003cul\u003e\n\u003cli\u003e\u003ca href="https://docs.netlify.com/"\u003eNetlify\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003e\u003ca href="https://gohugo.io/documentation/"\u003eHugo\u003c/a\u003e\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch2 id="can-i-get-support"\u003eCan I get support?\u003c/h2\u003e\n\u003cp\u003eCreate a topic:\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003e\u003ca href="https://community.netlify.com/"\u003eNetlify Community\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003e\u003ca href="https://discourse.gohugo.io/"\u003eHugo Forums\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003e\u003ca href="https://github.com/h-enk/doks/discussions"\u003eDoks Discussions\u003c/a\u003e\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch2 id="contact-the-creator"\u003eContact the creator?\u003c/h2\u003e\n\u003cp\u003eSend \u003ccode\u003eh-enk\u003c/code\u003e a message:\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003e\u003ca href="https://community.netlify.com/"\u003eNetlify Community\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003e\u003ca href="https://discourse.gohugo.io/"\u003eHugo Forums\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003e\u003ca href="https://github.com/h-enk/doks/discussions"\u003eDoks Discussions\u003c/a\u003e\u003c/li\u003e\n\u003c/ul\u003e\n'}).add({id:6,href:"https://realvincentyuan.github.io/Hugo_blog/docs/help/",title:"Help",description:"Help Doks.",content:""}).add({id:7,href:"https://realvincentyuan.github.io/Hugo_blog/docs/prologue/",title:"Prologue",description:"Prologue Doks.",content:""}).add({id:8,href:"https://realvincentyuan.github.io/Hugo_blog/docs/",title:"Docs",description:"Docs Doks.",content:""}),userinput.addEventListener('input',b,!0),suggestions.addEventListener('click',c,!0);function b(){var d,e;const c=5;d=this.value,e=a.search(d,{limit:c,enrich:!0}),suggestions.classList.remove('d-none'),suggestions.innerHTML="";const b={};e.forEach(a=>{a.result.forEach(a=>{b[a.doc.href]=a.doc})});for(const d in b){const e=b[d],a=document.createElement('div');if(a.innerHTML='<a href><span></span><span></span></a>',a.querySelector('a').href=d,a.querySelector('span:first-child').textContent=e.title,a.querySelector('span:nth-child(2)').textContent=e.description,suggestions.appendChild(a),suggestions.childElementCount==c)break}}function c(){while(suggestions.lastChild)suggestions.removeChild(suggestions.lastChild);return!1}})()